import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [cities, setCities] = useState([]);
  const [hoveredCity, setHoveredCity] = useState(null);

  useEffect(() => {
    // Fonction pour récupérer les données des villes depuis l'API
    const fetchData = async () => {
      try {
        const response = await axios.get('URL_DE_VOTRE_API');
        setCities(response.data);
      } catch (error) {
        console.error('Error fetching cities:', error);
      }
    };

    fetchData();
  }, []);

  const handleMapClick = (event) => {
    const rect = event.target.getBoundingClientRect();
    const x = (event.clientX - rect.left) / rect.width;
    const y = (event.clientY - rect.top) / rect.height;
    // Envoyer les coordonnées x et y au serveur pour récupérer les détails des villes à proximité
    console.log('Clicked at:', x, y);
  };

  const handleCityHover = (city) => {
    setHoveredCity(city);
  };

  return (
    <div className="App">
      <img src="carte.png" alt="Carte de la France" onClick={handleMapClick} useMap="#cities" />
      <map name="cities">
        {cities.map(city => (
          <area
            key={city.id}
            shape="circle"
            coords={`${city.x},${city.y},10`}
            onMouseOver={() => handleCityHover(city)}
            onMouseOut={() => handleCityHover(null)}
          />
        ))}
      </map>
      {hoveredCity && (
        <div className="city-details">
          <h2>{hoveredCity.name}</h2>
          {/* Calculer et afficher la distance du point ciblé par l'utilisateur */}
        </div>
      )}
    </div>
  );
}

export default App;







































