import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import carte from './carte.png';

function App() {
  const [cities, setCities] = useState([]);
  const [hoveredCity, setHoveredCity] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:8080/citys');
        setCities(response.data);
        console.log('Données des villes récupérées avec succès:', response.data);
      } catch (error) {
        console.error('Erreur lors de la récupération des données des villes:', error);
      }
    };

    fetchData();
  }, []);

  const handleMapClick = async (event) => {
    const rect = event.target.getBoundingClientRect();
    const x = (event.clientX - rect.left) / rect.width;
    const y = (event.clientY - rect.top) / rect.height;
    console.log('Clicked at:', x, y);

    try {
      const response = await axios.get(`http://localhost:8080/citys?x=${x}&y=${y}`);
      const citiesData = response.data;
      setCities(citiesData);
      console.log('Villes récupérées:', citiesData);
    } catch (error) {
      console.error('Erreur lors de la récupération des villes:', error);
    }
  };

  const handleCityHover = (city) => {
    setHoveredCity(city);
    console.log('Ville survolée:', city);
  };



  const renderCities = () => {
    return cities.map(city => (
      <div
        key={city.id}
        className="city-marker"
        style={{ left: `${city.x}px`, top: `${city.y}px` }} // Utilisez les coordonnées x et y des villes en pixels
        onMouseOver={() => handleCityHover(city)}
        onMouseOut={() => handleCityHover(null)}
      />
    ));
  };

  return (
    <div className="App">

    <div className="map-container">
      <img src={carte} alt="Carte de la France" onClick={handleMapClick} useMap="#cities" />
      <map name="cities">
        {cities.map(city => (
          <area
            key={city.id}
            shape="circle"
            coords={`${city.x},${city.y},10`}
            onMouseOver={() => handleCityHover(city)}
              onMouseOut={() => handleCityHover(null)}
            />
        ))}
      </map>
      {renderCities()}
      {hoveredCity && (
        <div className="city-details">
          <h2>{hoveredCity.name}</h2>
          <h2>{hoveredCity.region}</h2>
          {hoveredCity.distance !== undefined && (
      <p>Distance: {hoveredCity.distance.toFixed(2)} km</p>
      )}
        </div>

      )}

    </div>
    <div className="sidebar">
        <h3>Liste des Villes</h3>
        <ul>
          {cities.map(city => (
            <li key={city.id}>{city.name} [{city.distance !== undefined ? city.distance.toFixed(2) + ' km' : 'N/A'}]</li>
          ))}
      </ul>
    </div>
    </div>
  );
}

export default App;









































