import React from 'react';
import Map from './components/Map';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cityDetails: null,
    };
  }

  fetchCityDetails = (lat, lng) => {
    fetch(`http://localhost:8080/citys`)
      .then(response => response.json())
      .then(data => {
        this.setState({ cityDetails: data });
      })
      .catch(error => console.error('Erreur lors de la récupération des détails de la ville :', error));
  }

  render() {
    return (
      <div>
        <h1>Carte des villes françaises</h1>
        <Map onClick={this.fetchCityDetails} />
        {/* Ajouter le code pour afficher les détails de la ville */}
      </div>
    );
  }
}

export default App;









































