import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import carte from './carte.png';


function Map({ carte, fetchData, setSelectedCity }) {
  const [hoveredCity, setHoveredCity] = useState(null);

  const handleMapClick = (event) => {
    const rect = event.target.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;
    fetchData(x, y);
  };

  const handleMouseOver = async (event) => {
    const rect = event.target.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    try {
      const response = await fetch(`http://localhost:8080/citys?x=${x}&y=${y}`);
      const cityData = await response.json();
      setHoveredCity(cityData.name); // Suppose que votre réponse contient le nom de la ville
    } catch (error) {
      console.error('Erreur lors de la récupération des données de la ville:', error);
    }
  };

  const handleMouseOut = () => {
    setHoveredCity(null);
  };

  return (
    <div style={{ position: "relative" }}>
      <img 
        src={carte} 
        alt="Carte de France" 
        style={{ width: "50%", height: "auto", float:"left" }} 
        onClick={handleMapClick} 
        onMouseOver={handleMouseOver}
        onMouseOut={handleMouseOut}
      />
      {hoveredCity && <p>{hoveredCity}</p>}
    </div>
  );
}

export default Map;























